import * as React from "react";
import { PropertyControls, ControlType } from "framer";

type CursorType =
  | "beachball"
  | "busy"
  | "copy"
  | "cross"
  | "default"
  | "hand"
  | "pointer"
  | "help"
  | "move"
  | "text"
  | "zoom-in"
  | "zoom-out";

const allTypes = [
  "beachball",
  "busy",
  "copy",
  "cross",
  "default",
  "hand",
  "pointer",
  "help",
  "move",
  "text",
  "zoom-in",
  "zoom-out"
];

const allTypeTitles = [
  "Beachball",
  "Busy",
  "Copy",
  "Cross",
  "Default",
  "Hand",
  "Pointer",
  "Help",
  "Move",
  "Text",
  "Zoom In",
  "Zoom Out"
];

interface Props {
  type: CursorType;
  width: number;
  height: number;
}

export class Cursor extends React.Component<Props> {
  static defaultProps = {
    width: 32,
    height: 32,
    type: "default"
  };

  static propertyControls: PropertyControls<Props> = {
    type: {
      type: ControlType.Enum,
      options: allTypes,
      optionTitles: allTypeTitles,
      title: "Type"
    }
  };

  render() {
    const { width, height, type } = this.props;
    return (
      <div
        style={{
          width,
          height,
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
        dangerouslySetInnerHTML={{ __html: cursorImageForType(type) }}
      />
    );
  }
}

function cursorImageForType(type: CursorType) {
  switch (type) {
    case "beachball":
      return beachball;
    case "busy":
      return busy;
    case "copy":
      return copy;
    case "cross":
      return cross;
    case "default":
      return defaultCursor;
    case "hand":
      return hand;
    case "pointer":
      return pointer;
    case "help":
      return help;
    case "move":
      return move;
    case "text":
      return text;
    case "zoom-in":
      return zoomIn;
    case "zoom-out":
      return zoomOut;
  }
}

